PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE meta(key LONGVARCHAR NOT NULL UNIQUE PRIMARY KEY, value LONGVARCHAR);
CREATE TABLE conversation(uuid TEXT PRIMARY KEY NOT NULL,title BLOB,model_key TEXT,total_tokens INTEGER NOT NULL,trimmed_tokens INTEGER NOT NULL);
CREATE TABLE associated_content(uuid TEXT PRIMARY KEY NOT NULL,conversation_uuid TEXT NOT NULL,title BLOB,url BLOB,content_type INTEGER NOT NULL,last_contents BLOB,content_used_percentage INTEGER NOT NULL,conversation_entry_uuid TEXT NOT NULL DEFAULT '');
CREATE TABLE conversation_entry(uuid TEXT PRIMARY KEY NOT NULL,conversation_uuid STRING NOT NULL,date INTEGER NOT NULL,entry_text BLOB,prompt BLOB,character_type INTEGER NOT NULL,editing_entry_uuid TEXT,action_type INTEGER,selected_text BLOB, model_key TEXT,smart_mode_data BLOB);
CREATE TABLE conversation_entry_event_completion(conversation_entry_uuid INTEGER NOT NULL,event_order INTEGER NOT NULL,text BLOB NOT NULL,PRIMARY KEY(conversation_entry_uuid, event_order));
CREATE TABLE conversation_entry_event_search_queries(conversation_entry_uuid INTEGER NOT NULL,event_order INTEGER NOT NULL,queries BLOB NOT NULL,PRIMARY KEY(conversation_entry_uuid, event_order));
CREATE TABLE conversation_entry_uploaded_files(conversation_entry_uuid INTEGER NOT NULL,file_order INTEGER NOT NULL,filename BLOB NOT NULL,filesize INTEGER NOT NULL,data BLOB NOT NULL,type INTEGER NOT NULL,PRIMARY KEY(conversation_entry_uuid, file_order));
COMMIT;